import React, { useState } from 'react';
import '../styles/components/EmailModal.css';
import axios from 'axios';

const EmailModal = ({ professional, onClose, query }) => {
  const [emailContent, setEmailContent] = useState('');
  const [status, setStatus] = useState('');
  const [sending, setSending] = useState(false);
  
  const profName = professional.Name || professional['Doctor Name'] || 'Professional';
  
  const handleSendEmail = async () => {
    if (!emailContent.trim()) {
      setStatus('Please enter your message');
      return;
    }
    
    setSending(true);
    try {
      const emailAddress = professional.email || 
                         professional['Contact Number'] || 
                         professional.Phone || 
                         `${profName.replace(/\s+/g, '').toLowerCase()}@skillseeker.com`;
                         
      await axios.post('http://localhost:5000/api/email/send', {
        to: emailAddress,
        subject: `Query regarding ${query.substring(0, 30)}...`,
        content: emailContent
      });
      
      setStatus('Email sent successfully!');
      setTimeout(() => {
        onClose();
      }, 2000);
    } catch (err) {
      setStatus('Failed to send email: ' + (err.response?.data?.message || err.message));
    } finally {
      setSending(false);
    }
  };
  
  return (
    <div className="email-modal-overlay">
      <div className="email-modal">
        <h3>Contact {profName}</h3>
        <p>Send a message about your project or requirements:</p>
        
        <textarea
          value={emailContent}
          onChange={(e) => setEmailContent(e.target.value)}
          placeholder="Describe your needs in detail..."
          rows={6}
        />
        
        {status && <p className="status-message">{status}</p>}
        
        <div className="modal-buttons">
          <button onClick={onClose} className="cancel-button">Cancel</button>
          <button 
            onClick={handleSendEmail} 
            className="send-button"
            disabled={sending}
          >
            {sending ? 'Sending...' : 'Send Message'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default EmailModal;
