// src/pages/Dashboard.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { FaMapMarkerAlt, FaSearch, FaUserMd, FaLaptopCode, FaPencilAlt } from 'react-icons/fa';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { classifyQuery } from '../services/queryService';
import '../styles/Dashboard.css';

const Dashboard = () => {
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [userLocation, setUserLocation] = useState('');
  const [activeCategory, setActiveCategory] = useState('all');
  const navigate = useNavigate();

  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { duration: 0.5, when: "beforeChildren", staggerChildren: 0.2 }
    }
  };
  
  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: { y: 0, opacity: 1, transition: { duration: 0.4 } }
  };

  const cardVariants = {
    hidden: { scale: 0.95, opacity: 0 },
    visible: { scale: 1, opacity: 1, transition: { duration: 0.4 } },
    hover: { y: -10, boxShadow: "0 10px 20px rgba(0,0,0,0.1)" }
  };

  useEffect(() => {
    // Load user location from localStorage when component mounts
    const storedLocation = localStorage.getItem('userLocation');
    if (storedLocation) {
      setUserLocation(storedLocation);
    }
    
    // Add subtle background animation
    document.body.classList.add('dashboard-background');
    
    return () => {
      document.body.classList.remove('dashboard-background');
    };
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!query.trim()) {
      setError('Please enter a query');
      return;
    }
    
    setLoading(true);
    setError('');
    
    try {
      // Enhance query with location and category if available
      let enhancedQuery = query;
      if (userLocation) {
        enhancedQuery = `${query} in ${userLocation}`;
      }
      if (activeCategory !== 'all') {
        enhancedQuery = `${enhancedQuery} (category: ${activeCategory})`;
      }
      
      console.log(`Processing enhanced query: ${enhancedQuery}`);
      
      const response = await classifyQuery(enhancedQuery);
      
      // Add a small delay for the animation to complete
      setTimeout(() => {
        navigate('/classification', { 
          state: { 
            classifications: response.data.classifications,
            query: enhancedQuery
          }
        });
      }, 300);
    } 
    catch (err) {
      setError('Error processing query: ' + (err.response?.data?.message || err.message));
    } finally {
      setLoading(false);
    }
  };

  const categoryCards = [
    { id: 'medical', name: 'Medical', icon: <FaUserMd />, description: 'Find doctors, specialists, and healthcare professionals' },
    { id: 'technical', name: 'Technical', icon: <FaLaptopCode />, description: 'Connect with developers, engineers, and IT specialists' },
    { id: 'creative', name: 'Creative', icon: <FaPencilAlt />, description: 'Discover designers, writers, and creative experts' },
  ];

  return (
    <div className="dashboard-page">
      <Header />
      
      <main className="dashboard-main">
        <div className="dashboard-hero">
          <motion.div 
            className="hero-content"
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            <motion.h1 variants={itemVariants} className="hero-title">
              Find the Perfect Professional
            </motion.h1>
            <motion.p variants={itemVariants} className="hero-subtitle">
              Describe your needs, and we'll connect you with the right expert
            </motion.p>
            
            {userLocation && (
              <motion.div 
                className="location-badge"
                variants={itemVariants}
              >
                <FaMapMarkerAlt className="location-icon" />
                <span>{userLocation}</span>
                <button 
                  className="change-location-button"
                  onClick={() => navigate('/location')}
                >
                  Change
                </button>
              </motion.div>
            )}
          </motion.div>
        </div>
        
        <motion.div 
          className="search-container"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          <motion.div className="category-tabs" variants={itemVariants}>
            <button 
              className={`category-tab ${activeCategory === 'all' ? 'active' : ''}`} 
              onClick={() => setActiveCategory('all')}
            >
              All Categories
            </button>
            {categoryCards.map(category => (
              <button 
                key={category.id} 
                className={`category-tab ${activeCategory === category.id ? 'active' : ''}`}
                onClick={() => setActiveCategory(category.id)}
              >
                <span className="tab-icon">{category.icon}</span>
                {category.name}
              </button>
            ))}
          </motion.div>
          
          {error && (
            <motion.div 
              className="error-alert"
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="error-icon" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
              </svg>
              {error}
            </motion.div>
          )}
          
          <motion.form 
            onSubmit={handleSubmit} 
            className="search-form"
            variants={itemVariants}
          >
            <div className="textarea-container">
              <textarea
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder={activeCategory === 'all' 
                  ? "E.g., I need a specialist who can help me with..." 
                  : `E.g., I need a ${activeCategory === 'medical' ? 'doctor who specializes in...' : 
                      activeCategory === 'technical' ? 'developer who can build...' : 
                      'designer who can create...'}`
                }
                rows={4}
                required
              />
              {query.length > 0 && (
                <button 
                  type="button" 
                  className="clear-button" 
                  onClick={() => setQuery('')}
                >
                  √ó
                </button>
              )}
            </div>
            <motion.button 
              type="submit" 
              className="search-button"
              disabled={loading}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              {loading ? (
                <div className="loading-container">
                  <div className="loading-spinner"></div>
                  <span>Searching...</span>
                </div>
              ) : (
                <>
                  <FaSearch className="search-icon" />
                  <span>Find Professionals</span>
                </>
              )}
            </motion.button>
          </motion.form>
        </motion.div>
        
        <motion.div 
          className="category-cards"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          {categoryCards.map((category) => (
            <motion.div
              key={category.id}
              className="category-card"
              variants={cardVariants}
              whileHover="hover"
              onClick={() => setActiveCategory(category.id)}
            >
              <div className="card-icon">{category.icon}</div>
              <h3>{category.name}</h3>
              <p>{category.description}</p>
            </motion.div>
          ))}
        </motion.div>
        
        <motion.div 
          className="features-section"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          <motion.h2 variants={itemVariants}>Why Choose Skill-Seeker?</motion.h2>
          <div className="features-grid">
            <motion.div className="feature-card" variants={itemVariants}>
              <div className="feature-icon">üîç</div>
              <h3>Smart Matching</h3>
              <p>Our AI-powered system finds the perfect professional based on your specific requirements</p>
            </motion.div>
            <motion.div className="feature-card" variants={itemVariants}>
              <div className="feature-icon">‚≠ê</div>
              <h3>Verified Experts</h3>
              <p>All professionals go through a rigorous verification process</p>
            </motion.div>
            <motion.div className="feature-card" variants={itemVariants}>
              <div className="feature-icon">üõ°Ô∏è</div>
              <h3>Secure Platform</h3>
              <p>Your data is protected and your transactions are secure</p>
            </motion.div>
          </div>
        </motion.div>
      </main>
      
      <Footer />
    </div>
  );
};

export default Dashboard;
