import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { classifyQuery } from '../services/queryService';
import '../styles/Dashboard.css';

const Dashboard = () => {
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!query.trim()) {
      setError('Please enter a query');
      return;
    }
    
    setLoading(true);
    setError('');
    
    try {
      const response = await classifyQuery(query);
      navigate('/classification', { 
        state: { 
          classifications: response.data.classifications,
          query 
        }
      });
    } catch (err) {
      setError('Error processing query: ' + (err.response?.data?.message || err.message));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="dashboard-page">
      <Header />
      
      <main className="dashboard-container">
        <div className="dashboard-content">
          <h2>Find the perfect professional for your needs</h2>
          <p>Describe your project or requirements, and we'll match you with the best experts.</p>
          
          {error && <div className="error-message">{error}</div>}
          
          <form onSubmit={handleSubmit} className="query-form">
            <textarea
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="E.g., I need a doctor who specializes in pediatric neurology in London..."
              rows={5}
              required
            />
            <button type="submit" disabled={loading}>
              {loading ? 'Processing...' : 'Find Professionals'}
            </button>
          </form>
        </div>
      </main>
      
      <Footer />
    </div>
  );
};

export default Dashboard;
