import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/components/Header.css';

const Header = () => {
  const navigate = useNavigate();
  const [showLocationModal, setShowLocationModal] = useState(false);
  const [userLocation, setUserLocation] = useState('');
  
  useEffect(() => {
    // Load location when component mounts
    const storedLocation = localStorage.getItem('userLocation');
    if (storedLocation) {
      setUserLocation(storedLocation);
    }
  }, []);
  
  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };
  
  return (
    <header className="header">
      <div className="logo" onClick={() => navigate('/dashboard')}>
        <h1>Skill Seeker</h1>
      </div>
      <div className="header-actions">
        {userLocation && (
          <div className="location-display">
            <span>Location: <strong>{userLocation}</strong></span>
            <button className="header-btn location-btn" onClick={() => setShowLocationModal(true)}>
              Change
            </button>
          </div>
        )}
        <button className="header-btn dashboard-btn" onClick={() => navigate('/dashboard')}>
          Dashboard
        </button>
        <button className="header-btn logout-btn" onClick={handleLogout}>
          Logout
        </button>
      </div>
      
      {/* Location Update Modal */}
      {showLocationModal && (
        <LocationModal 
          currentLocation={userLocation}
          onClose={() => setShowLocationModal(false)}
          onUpdate={(newLocation) => {
            setUserLocation(newLocation);
            localStorage.setItem('userLocation', newLocation);
            setShowLocationModal(false);
          }}
        />
      )}
    </header>
  );
};

// Location Modal Component
const LocationModal = ({ currentLocation, onClose, onUpdate }) => {
  const [newLocation, setNewLocation] = useState(currentLocation || '');
  
  const handleSubmit = (e) => {
    e.preventDefault();
    if (newLocation.trim()) {
      onUpdate(newLocation);
    }
  };
  
  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3>Update Your Location</h3>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Your Location (City)</label>
            <input
              type="text"
              value={newLocation}
              onChange={(e) => setNewLocation(e.target.value)}
              placeholder="e.g., Islamabad"
            />
          </div>
          <div className="modal-buttons">
            <button type="submit" className="save-button">Save</button>
            <button type="button" className="cancel-button" onClick={onClose}>
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Header;
