import React, { useState, useRef, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import ProfessionalCard from '../components/ProfessionalCard';
import SkillsChart from '../components/SkillsChart';
import EmailModal from '../components/EmailModal';
import Loading from '../components/Loading';
import { getConversationalResponse } from '../services/queryService';
import '../styles/ProfessionalsResults.css';

const ProfessionalsResults = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { professionals, category, query, synthesis, followUpQA } = location.state || {};
  
  const [selectedProfessional, setSelectedProfessional] = useState(null);
  const [showEmailModal, setShowEmailModal] = useState(false);
  const [conversation, setConversation] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState('');
  const [loading, setLoading] = useState(false);
  
  const conversationEndRef = useRef(null);
  
  useEffect(() => {
    // Scroll to bottom of conversation whenever it updates
    if (conversationEndRef.current) {
      conversationEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [conversation]);
  
  useEffect(() => {
    // Select first professional by default
    if (professionals && professionals.length > 0 && !selectedProfessional) {
      setSelectedProfessional(professionals[0]);
    }
  }, [professionals, selectedProfessional]);
  
  // Function to ask follow-up questions to the model
  const askQuestion = async () => {
    if (!currentQuestion.trim() || loading) return;
    
    setLoading(true);
    try {
      // Add user question to conversation
      const newConversation = [...conversation, { 
        type: 'question', 
        text: currentQuestion 
      }];
      
      setConversation(newConversation);
      setCurrentQuestion('');
      
      // Get response from model
      const response = await getConversationalResponse({
        category,
        query,
        synthesis,
        professionals,
        followUpQA,
        question: currentQuestion
      });
      
      // Add model response to conversation
      setConversation([...newConversation, { 
        type: 'answer', 
        text: response.data.response 
      }]);
    } catch (err) {
      setConversation([...conversation, 
        { type: 'question', text: currentQuestion },
        { type: 'error', text: 'Sorry, there was an error processing your question. Please try again.' }
      ]);
    } finally {
      setLoading(false);
    }
  };
  
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      askQuestion();
    }
  };
  
  if (!professionals || professionals.length === 0) {
    return (
      <div className="professionals-page">
        <Header />
        <div className="error-container">
          <h2>No professionals found</h2>
          <p>Please go back to the dashboard and try again.</p>
          <button onClick={() => navigate('/dashboard')}>Back to Dashboard</button>
        </div>
        <Footer />
      </div>
    );
  }
  
  return (
    <div className="professionals-page">
      <Header />
      
      <main className="results-container">
        <div className="sidebar">
          <div className="query-summary">
            <h3>{category.replace(' Classifier', '')}</h3>
            <p>{synthesis}</p>
          </div>
          
          <div className="professionals-list">
            <h3>Recommended Professionals</h3>
            {professionals.map((professional, index) => (
              <ProfessionalCard
                key={index}
                professional={professional}
                isSelected={selectedProfessional === professional}
                onClick={() => setSelectedProfessional(professional)}
              />
            ))}
          </div>
        </div>
        
        <div className="main-content">
          {selectedProfessional ? (
            <div className="professional-details">
              <div className="details-header">
                <h2>{selectedProfessional.Name || selectedProfessional['Doctor Name'] || 'Professional'}</h2>
                <button 
                  className="contact-button"
                  onClick={() => setShowEmailModal(true)}
                >
                  Contact Now
                </button>
              </div>
              
              <div className="details-grid">
                <div className="details-section">
                  <h3>Professional Information</h3>
                  <p><strong>Expertise:</strong> {selectedProfessional.Expertise || selectedProfessional.Skills || selectedProfessional.Category || 'Specialized Professional'}</p>
                  <p><strong>Experience:</strong> {selectedProfessional.Years_of_Experience || '8+ years'}</p>
                  <p><strong>Location:</strong> {selectedProfessional.City || (selectedProfessional.Locations && selectedProfessional.Locations[0]) || 'London, UK'}</p>
                  
                  {selectedProfessional.About && (
                    <div className="about-section">
                      <h4>About</h4>
                      <p>{selectedProfessional.About}</p>
                    </div>
                  )}
                  
                  {selectedProfessional['Programming Languages'] && (
                    <div className="skills-section">
                      <h4>Technical Skills</h4>
                      <p><strong>Languages:</strong> {selectedProfessional['Programming Languages']}</p>
                      <p><strong>Frameworks:</strong> {selectedProfessional.Frameworks}</p>
                      <p><strong>Tools:</strong> {selectedProfessional.Tools}</p>
                    </div>
                  )}
                  
                  {selectedProfessional.Education && (
                    <div className="education-section">
                      <h4>Education</h4>
                      <p>{selectedProfessional.Education}</p>
                    </div>
                  )}
                </div>
                
                <SkillsChart professional={selectedProfessional} category={category} />
              </div>
              
              <div className="packages-section">
                <h3>Service Packages</h3>
                <div className="package-card">
                  <h4>{selectedProfessional['Package Title'] || 'Standard Consultation'}</h4>
                  <p className="price">{selectedProfessional['Package Price'] || '$150'}</p>
                  <p>Initial consultation and preliminary assessment of your needs.</p>
                </div>
              </div>
              
              <div className="reviews-section">
                <h3>Client Feedback</h3>
                <div className="review-card">
                  <div className="review-header">
                    <span className="review-stars">★★★★★</span>
                    <span className="review-date">March 2025</span>
                  </div>
                  <p>{selectedProfessional['Review Summary'] || selectedProfessional.Review || "Excellent service! Very professional and responsive. Would definitely recommend."}</p>
                </div>
              </div>
            </div>
          ) : (
            <div className="select-prompt">
              <h2>Select a professional from the list</h2>
              <p>Click on any professional to view their detailed profile</p>
            </div>
          )}
          
          <div className="conversation-section">
            <h3>Ask Additional Questions</h3>
            <div className="conversation-history">
              {conversation.map((item, index) => (
                <div key={index} className={`conversation-item ${item.type}`}>
                  {item.type === 'question' ? (
                    <div className="user-question">
                      <span className="user-icon">👤</span>
                      <p>{item.text}</p>
                    </div>
                  ) : item.type === 'error' ? (
                    <div className="error-message">
                      <p>{item.text}</p>
                    </div>
                  ) : (
                    <div className="model-answer">
                      <span className="model-icon">🤖</span>
                      <p>{item.text}</p>
                    </div>
                  )}
                </div>
              ))}
              {loading && (
                <div className="typing-indicator">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              )}
              <div ref={conversationEndRef} />
            </div>
            <div className="question-input">
              <textarea
                value={currentQuestion}
                onChange={(e) => setCurrentQuestion(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Ask a question about the project or professional..."
                disabled={loading}
                rows={2}
              />
              <button 
                onClick={askQuestion}
                disabled={!currentQuestion.trim() || loading}
              >
                Send
              </button>
            </div>
          </div>
        </div>
      </main>
      
      <Footer />
      
      {showEmailModal && (
        <EmailModal 
          professional={selectedProfessional}
          query={query}
          onClose={() => setShowEmailModal(false)}
        />
      )}
      
      {loading && <Loading message="Processing your question..." />}
    </div>
  );
};

export default ProfessionalsResults;
