import React from 'react';
import '../styles/components/Loading.css';

const Loading = ({ message }) => {
  return (
    <div className="loading-container">
      <div className="loading-spinner"></div>
      <p>{message || 'Loading...'}</p>
    </div>
  );
};

export default Loading;
