import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';

const Header = () => {
  const navigate = useNavigate();
  const [showLocationModal, setShowLocationModal] = useState(false);
  const [userLocation, setUserLocation] = useState('');

  useEffect(() => {
    const storedLocation = localStorage.getItem('userLocation');
    if (storedLocation) {
      setUserLocation(storedLocation);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <header className="flex justify-between items-center p-4 bg-gray-900 text-white shadow-lg">
      <div className="text-2xl font-bold cursor-pointer" onClick={() => navigate('/dashboard')}>
        Skill Seeker
      </div>
      <div className="flex items-center gap-4">
        {userLocation && (
          <div className="text-lg">Location: <strong>{userLocation}</strong></div>
        )}
        <AnimatedButton text="Change" onClick={() => setShowLocationModal(true)} />
        <AnimatedButton text="Dashboard" onClick={() => navigate('/dashboard')} />
        <AnimatedButton text="Logout" onClick={handleLogout} />
      </div>

      {showLocationModal && (
        <LocationModal 
          currentLocation={userLocation}
          onClose={() => setShowLocationModal(false)}
          onUpdate={(newLocation) => {
            setUserLocation(newLocation);
            localStorage.setItem('userLocation', newLocation);
            setShowLocationModal(false);
          }}
        />
      )}
    </header>
  );
};

const AnimatedButton = ({ text, onClick }) => {
  return (
    <motion.button
      whileHover={{ scale: 1.1 }}
      whileTap={{ scale: 0.9 }}
      className="px-4 py-2 rounded-lg bg-blue-600 hover:bg-blue-500 transition duration-300 shadow-md"
      onClick={onClick}
    >
      {text}
    </motion.button>
  );
};

const LocationModal = ({ currentLocation, onClose, onUpdate }) => {
  const [newLocation, setNewLocation] = useState(currentLocation || '');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newLocation.trim()) {
      onUpdate(newLocation);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
      <motion.div 
        initial={{ opacity: 0, y: -50 }} 
        animate={{ opacity: 1, y: 0 }} 
        exit={{ opacity: 0, y: -50 }}
        className="bg-white p-6 rounded-lg shadow-lg w-1/3 text-center"
      >
        <h3 className="text-xl font-semibold mb-4">Update Your Location</h3>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={newLocation}
            onChange={(e) => setNewLocation(e.target.value)}
            className="w-full p-2 border rounded-md mb-4"
            placeholder="e.g., Islamabad"
          />
          <div className="flex justify-around">
            <AnimatedButton text="Save" onClick={handleSubmit} />
            <AnimatedButton text="Cancel" onClick={onClose} />
          </div>
        </form>
      </motion.div>
    </div>
  );
};

export default Header;
