import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles/UpdateLocation.css';

const UpdateLocation = () => {
  const [location, setLocation] = useState('');
  const navigate = useNavigate();
  
  useEffect(() => {
    // Load current location
    const storedLocation = localStorage.getItem('userLocation');
    if (storedLocation) {
      setLocation(storedLocation);
    }
  }, []);
  
  const handleSubmit = (e) => {
    e.preventDefault();
    if (location.trim()) {
      localStorage.setItem('userLocation', location);
      navigate('/dashboard');
    }
  };
  
  return (
    <div className="update-location-page">
      <Header />
      
      <main className="update-location-container">
        <div className="update-location-content">
          <h2>Update Your Location</h2>
          <p>Your location helps us find professionals in your area.</p>
          
          <form onSubmit={handleSubmit} className="location-form">
            <div className="form-group">
              <label>Your Location (City)</label>
              <input
                type="text"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                placeholder="e.g., Islamabad"
                required
              />
            </div>
            <div className="button-group">
              <button type="submit" className="save-button">
                Save Location
              </button>
              <button 
                type="button" 
                className="cancel-button"
                onClick={() => navigate('/dashboard')}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      </main>
      
      <Footer />
    </div>
  );
};

export default UpdateLocation;
