import React from 'react';
import { Radar } from 'react-chartjs-2';
import '../styles/components/SkillsChart.css';

const SkillsChart = ({ professional, category }) => {
  const getSkillsData = () => {
    let labels = [];
    let data = [];
    
    if (category.includes('Medical')) {
      labels = ['Experience', 'Specialization', 'Patient Reviews', 'Research', 'Availability'];
      data = [
        professional.Years_of_Experience ? parseFloat(professional.Years_of_Experience) / 10 * 100 : 70,
        85, // Specialization relevance
        professional.Rating ? parseFloat(professional.Rating) * 20 : 75,
        65, // Research contributions
        80  // Availability
      ];
    } else if (category.includes('Legal')) {
      labels = ['Experience', 'Success Rate', 'Client Reviews', 'Specialization', 'Cost Efficiency'];
      data = [
        professional.Years_of_Experience ? parseFloat(professional.Years_of_Experience) / 10 * 100 : 70,
        professional.Success_Rate ? parseFloat(professional.Success_Rate) : 75,
        85, // Client reviews
        90, // Specialization relevance
        75  // Cost efficiency
      ];
    } else if (category.includes('CS')) {
      labels = ['Technical Skills', 'Experience', 'Client Rating', 'Communication', 'Project Relevance'];
      data = [
        90, // Technical skills
        75, // Experience
        professional.Rating ? parseFloat(professional.Rating) * 20 : 80,
        85, // Communication
        95  // Project relevance
      ];
    } else if (category.includes('SEO')) {
      labels = ['Industry Knowledge', 'Results', 'Client Satisfaction', 'Tools Proficiency', 'Communication'];
      data = [
        85, // Industry knowledge
        80, // Results
        professional.Rating ? parseFloat(professional.Rating) * 20 : 75,
        90, // Tools proficiency
        85  // Communication
      ];
    }
    
    return {
      labels,
      datasets: [
        {
          label: 'Skills Assessment',
          data,
          backgroundColor: 'rgba(105, 105, 105, 0.2)',
          borderColor: 'rgba(105, 105, 105, 1)',
          borderWidth: 1
        }
      ]
    };
  };
  
  const options = {
    scales: {
      r: {
        angleLines: {
          display: true
        },
        suggestedMin: 0,
        suggestedMax: 100
      }
    }
  };
  
  return (
    <div className="skills-chart">
      <h3>Skills Assessment</h3>
      <Radar data={getSkillsData()} options={options} />
    </div>
  );
};

export default SkillsChart;
