import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import '../styles/LegalProfessionals.css';

const LegalProfessionals = () => {
  const [selectedProfessional, setSelectedProfessional] = useState(null);
  const [showAskQuestion, setShowAskQuestion] = useState(false);
  const [question, setQuestion] = useState('');
  const [messages, setMessages] = useState([]);
  
  // Sample professionals data
  const professionals = [
    {
      id: 1,
      name: 'Syed Law Associates',
      specialty: 'Criminal Defense',
      expertise: 'Criminal Defense, Trial Advocacy, Legal Research, Negotiation, Case Analysis, Courtroom Litigation, Evidence Presentation, Client Counseling',
      experience: '15 years',
      location: 'Bannu',
      rating: 89,
      contact: '923156821755',
      skills: {
        expertise: 90,
        caseSuccess: 85,
        experience: 88,
        reputation: 87,
        clientReviews: 89
      }
    },
    {
      id: 2,
      name: 'Usman Legal',
      specialty: 'Crime',
      expertise: 'Criminal Law, Homicide Defense, Legal Strategy, Evidence Analysis',
      experience: '12 years',
      location: 'Rawalpindi',
      rating: 90,
      contact: '953917672544',
      skills: {
        expertise: 92,
        caseSuccess: 90,
        experience: 82,
        reputation: 91,
        clientReviews: 88
      }
    },
    {
      id: 3,
      name: 'Pak-Lawyer Associates',
      specialty: 'Crime',
      expertise: 'Criminal Defense, Case Management, Court Representation',
      experience: '10 years',
      location: 'Islamabad',
      rating: 89,
      contact: '923214540992',
      skills: {
        expertise: 87,
        caseSuccess: 89,
        experience: 80,
        reputation: 85,
        clientReviews: 91
      }
    },
    {
      id: 4,
      name: 'Zafar Legal Services',
      specialty: 'Criminal Law',
      expertise: 'Criminal Defense, Murder Cases, Legal Consultation',
      experience: '18 years',
      location: 'Lahore',
      rating: 92,
      contact: '923008765432',
      skills: {
        expertise: 94,
        caseSuccess: 91,
        experience: 93,
        reputation: 89,
        clientReviews: 86
      }
    },
    {
      id: 5,
      name: 'Malik & Partners',
      specialty: 'Criminal Defense',
      expertise: 'Criminal Law, Homicide Defense, Evidence Handling',
      experience: '14 years',
      location: 'Karachi',
      rating: 87,
      contact: '923157654321',
      skills: {
        expertise: 85,
        caseSuccess: 84,
        experience: 87,
        reputation: 83,
        clientReviews: 85
      }
    }
  ];

  const handleProfessionalClick = (professional) => {
    setSelectedProfessional(professional);
  };

  const handleCloseModal = () => {
    setSelectedProfessional(null);
  };

  const handleSendQuestion = (e) => {
    e.preventDefault();
    if (!question.trim()) return;

    // Add user question to messages
    setMessages([...messages, { type: 'user', content: question }]);
    
    // Simulate AI response after a delay
    setTimeout(() => {
      const aiResponse = "Hello, I understand you're asking \"" + question + "\". While I can provide information, I am programmed to be a helpful AI assistant. For this specific legal matter, I recommend consulting directly with the selected attorney for professional advice.";
      setMessages(prev => [...prev, { type: 'ai', content: aiResponse }]);
    }, 1000);
    
    setQuestion('');
  };

  const handleContactNow = () => {
    alert(`Contacting ${selectedProfessional.name} at ${selectedProfessional.contact}`);
  };

  // Render a radar chart for skills visualization
  const renderSkillsRadar = (skills) => {
    // Calculate points for the radar chart (pentagon shape)
    const skillsKeys = ['expertise', 'caseSuccess', 'experience', 'reputation', 'clientReviews'];
    const centerX = 100;
    const centerY = 100;
    const radius = 80;
    
    // Generate path for the full pentagon background
    const fullPentagonPoints = skillsKeys.map((_, i) => {
      const angle = (Math.PI * 2 * i) / skillsKeys.length - Math.PI / 2;
      const x = centerX + radius * Math.cos(angle);
      const y = centerY + radius * Math.sin(angle);
      return `${x},${y}`;
    }).join(' ');
    
    // Generate path for the skills level
    const skillsPentagonPoints = skillsKeys.map((key, i) => {
      const angle = (Math.PI * 2 * i) / skillsKeys.length - Math.PI / 2;
      const skillValue = skills[key] / 100; // Normalize to 0-1
      const x = centerX + radius * skillValue * Math.cos(angle);
      const y = centerY + radius * skillValue * Math.sin(angle);
      return `${x},${y}`;
    }).join(' ');
    
    // Generate axis lines and labels
    const axisLines = skillsKeys.map((key, i) => {
      const angle = (Math.PI * 2 * i) / skillsKeys.length - Math.PI / 2;
      const x = centerX + radius * Math.cos(angle);
      const y = centerY + radius * Math.sin(angle);
      
      return (
        <g key={key}>
          <line 
            x1={centerX} 
            y1={centerY} 
            x2={x} 
            y2={y}
            stroke="#e5e7eb" 
            strokeWidth="1"
          />
          <text 
            x={centerX + (radius + 20) * Math.cos(angle)} 
            y={centerY + (radius + 20) * Math.sin(angle)}
            textAnchor="middle" 
            dominantBaseline="middle"
            fontSize="12"
            fill="#6b7280"
          >
            {key.charAt(0).toUpperCase() + key.slice(1).replace(/([A-Z])/g, ' $1')}
          </text>
        </g>
      );
    });
    
    // Generate web lines (concentric pentagons)
    const webLines = [20, 40, 60, 80, 100].map(percent => {
      const webPoints = skillsKeys.map((_, i) => {
        const angle = (Math.PI * 2 * i) / skillsKeys.length - Math.PI / 2;
        const webRadius = radius * (percent / 100);
        const x = centerX + webRadius * Math.cos(angle);
        const y = centerY + webRadius * Math.sin(angle);
        return `${x},${y}`;
      }).join(' ');
      
      return (
        <g key={percent}>
          <polygon 
            points={webPoints} 
            fill="none" 
            stroke="#e5e7eb" 
            strokeWidth="1"
          />
          <text 
            x={centerX} 
            y={centerY - (radius * percent / 100)}
            textAnchor="middle" 
            dominantBaseline="middle"
            fontSize="10"
            fill="#9ca3af"
          >
            {percent}
          </text>
        </g>
      );
    });
    
    return (
      <svg width="200" height="200" viewBox="0 0 200 200" className="skills-radar">
        {/* Background web and axis lines */}
        {webLines}
        {axisLines}
        
        {/* Skills polygon */}
        <polygon 
          points={skillsPentagonPoints} 
          fill="rgba(59, 130, 246, 0.3)" 
          stroke="#3b82f6" 
          strokeWidth="2"
        />
      </svg>
    );
  };

  // Render rating stars
  const renderStars = (rating) => {
    const starCount = 5;
    const fullStars = Math.floor(rating / 20);
    const hasHalfStar = (rating % 20) >= 10;
    
    return (
      <div className="stars-container">
        {[...Array(starCount)].map((_, i) => (
          <span key={i} className={`star ${i < fullStars ? 'full' : (i === fullStars && hasHalfStar ? 'half' : '')}`}>
            ★
          </span>
        ))}
        <span className="rating-percent">{rating}%</span>
      </div>
    );
  };

  return (
    <div className="legal-professionals-page">
      {/* Top Navigation Tabs */}
      <div className="top-tabs">
        <div className="tab active">Legal</div>
        <div className="tab">Medical</div>
        <div className="tab">Financial</div>
        <div className="tab">Technical</div>
      </div>
      
      <div className="content-container">
        {/* Legal explanation panel */}
        <div className="legal-explanation">
          <h2>Legal</h2>
          <div className="divider"></div>
          <p>
            <strong>**Criminal Law**</strong> is the most relevant legal category here. The user's statement, "I killed someone," indicates a potential charge of homicide, which encompasses various degrees of murder and manslaughter. The specific charges and penalties will depend on the circumstances surrounding the act.
          </p>
        </div>
        
        <div className="main-content">
          {/* Left panel - Professional list */}
          <div className="professionals-list">
            <h2>Recommended Professionals</h2>
            <div className="divider"></div>
            <div className="professionals-container">
              {professionals.map((professional) => (
                <div 
                  key={professional.id} 
                  className="professional-card"
                  onClick={() => handleProfessionalClick(professional)}
                >
                  <h3>{professional.name}</h3>
                  <p className="specialty">{professional.specialty}</p>
                  <div className="professional-rating">
                    {renderStars(professional.rating)}
                  </div>
                  <p className="professional-location">{professional.location}</p>
                </div>
              ))}
            </div>
          </div>
          
          {/* Right panel - Ask a question tab */}
          <div className="question-tab-container">
            <div 
              className={`question-tab ${showAskQuestion ? 'active' : ''}`}
              onClick={() => setShowAskQuestion(!showAskQuestion)}
            >
              <span className="question-tab-icon">❓</span>
              <span className="question-tab-text">Ask Additional Questions</span>
            </div>
            
            <AnimatePresence>
              {showAskQuestion && (
                <motion.div 
                  className="question-panel"
                  initial={{ x: '100%' }}
                  animate={{ x: 0 }}
                  exit={{ x: '100%' }}
                  transition={{ type: 'spring', stiffness: 300, damping: 30 }}
                >
                  <div className="question-panel-header">
                    <h3>Ask Your Question</h3>
                    <button 
                      className="close-panel-button"
                      onClick={() => setShowAskQuestion(false)}
                    >
                      ✕
                    </button>
                  </div>
                  
                  <div className="messages-container">
                    {messages.map((message, index) => (
                      <div key={index} className={`message ${message.type}`}>
                        <div className={`message-icon ${message.type}`}>
                          {message.type === 'user' ? '👤' : '🤖'}
                        </div>
                        <div className="message-content">
                          {message.content}
                        </div>
                      </div>
                    ))}
                  </div>
                  
                  <form className="question-form" onSubmit={handleSendQuestion}>
                    <textarea
                      value={question}
                      onChange={(e) => setQuestion(e.target.value)}
                      placeholder="Type your question here..."
                      rows={3}
                    />
                    <button type="submit" className="send-button">
                      <span>Send</span>
                    </button>
                  </form>
                </motion.div>
              )}
            </AnimatePresence>
          </div>
        </div>
      </div>
      
      {/* Professional details modal popup */}
      <AnimatePresence>
        {selectedProfessional && (
          <motion.div 
            className="modal-backdrop"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={handleCloseModal}
          >
            <motion.div 
              className="professional-modal"
              initial={{ scale: 0.9, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.9, opacity: 0 }}
              transition={{ type: 'spring', damping: 25 }}
              onClick={(e) => e.stopPropagation()}
            >
              <button className="modal-close-button" onClick={handleCloseModal}>✕</button>
              
              <div className="modal-header">
                <h2>{selectedProfessional.name}</h2>
                <button className="contact-button" onClick={handleContactNow}>
                  Contact Now
                </button>
              </div>
              
              <div className="modal-content">
                <div className="professional-info">
                  <h3>Professional Information</h3>
                  <div className="info-divider"></div>
                  
                  <div className="info-item">
                    <span className="info-label">Expertise:</span>
                    <span className="info-value">{selectedProfessional.expertise}</span>
                  </div>
                  
                  <div className="info-item">
                    <span className="info-label">Experience:</span>
                    <span className="info-value">{selectedProfessional.experience}</span>
                  </div>
                  
                  <div className="info-item">
                    <span className="info-label">Location:</span>
                    <span className="info-value">{selectedProfessional.location}</span>
                  </div>
                  
                  <div className="info-item">
                    <span className="info-label">Rating:</span>
                    <span className="info-value">{renderStars(selectedProfessional.rating)}</span>
                  </div>
                  
                  <div className="info-item">
                    <span className="info-label">Contact:</span>
                    <span className="info-value">{selectedProfessional.contact}</span>
                  </div>
                </div>
                
                <div className="skills-profile">
                  <h3>Skills Profile</h3>
                  <div className="info-divider"></div>
                  
                  <div className="skills-chart">
                    <h4>Professional Skills Profile</h4>
                    {renderSkillsRadar(selectedProfessional.skills)}
                  </div>
                </div>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default LegalProfessionals;
