import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import Loading from '../components/Loading';
import '../styles/ClassificationResults.css';

const ClassificationResults = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { classifications, query } = location.state || {};
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [loading, setLoading] = useState(false);
  
  // Extract valid categories (those that aren't "Other")
  const validCategories = classifications ? Object.entries(classifications)
    .filter(([_, result]) => result !== "Other")
    .map(([category]) => category) : [];
  
  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
  };
  
  const handleContinue = () => {
    if (selectedCategory) {
      setLoading(true);
      navigate('/processing', { state: { category: selectedCategory, query } });
    }
  };
  
  if (!classifications) {
    return (
      <div className="classification-page">
        <Header />
        <div className="error-container">
          <h2>No classification data available</h2>
          <p>Please go back to the dashboard and try again.</p>
          <button onClick={() => navigate('/dashboard')}>Back to Dashboard</button>
        </div>
        <Footer />
      </div>
    );
  }
  
  return (
    <div className="classification-page">
      <Header />
      
      <main className="classification-container">
        <h2>Query Classification Results</h2>
        <p className="query-text">Your query: "{query}"</p>
        
        <div className="classification-results">
          <h3>Your query matches these categories:</h3>
          {validCategories.length > 0 ? (
            <div className="category-options">
              {validCategories.map((category) => (
                <div 
                  key={category}
                  className={`category-card ${selectedCategory === category ? 'selected' : ''}`}
                  onClick={() => handleCategorySelect(category)}
                >
                  <h4>{category.replace(' Classifier', '')}</h4>
                  <p>Specialists in this field can help with your query.</p>
                </div>
              ))}
            </div>
          ) : (
            <div className="no-results">
              <p>No matching categories found. Please try refining your query.</p>
              <button onClick={() => navigate('/dashboard')}>Try Again</button>
            </div>
          )}
        </div>
        
        {selectedCategory && (
          <div className="action-buttons">
            <button 
              onClick={handleContinue} 
              className="continue-button"
              disabled={loading}
            >
              {loading ? 'Processing...' : `Continue with ${selectedCategory.replace(' Classifier', '')}`}
            </button>
          </div>
        )}
      </main>
      
      <Footer />
      
      {loading && <Loading message="Processing your query..." />}
    </div>
  );
};

export default ClassificationResults;
