// src/pages/Login.js
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion'; // Add this import
import { login } from '../services/authService';
import '../styles/Auth.css';
import CryptoJS from 'crypto-js';

const Login = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { duration: 0.5, when: "beforeChildren", staggerChildren: 0.2 }
    }
  };
  
  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: { y: 0, opacity: 1, transition: { duration: 0.4 } }
  };

  useEffect(() => {
    // Check if user is already logged in
    const userData = localStorage.getItem('userData');
    if (userData) {
      navigate('/dashboard');
    }
    
    // Add subtle pulsing animation to the dots
    const interval = setInterval(() => {
      const dots = document.querySelectorAll('.dot');
      dots.forEach(dot => {
        dot.classList.toggle('pulse');
      });
    }, 1500);
    
    return () => clearInterval(interval);
  }, [navigate]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      // Add loading animation to button
      const button = e.target.querySelector('button');
      button.classList.add('loading');
      
      // Hash password before sending to server
      const loginData = {
        username: formData.username,
        password: CryptoJS.SHA256(formData.password).toString()
      };
      
      const userData = await login(loginData);
      
      // Store user data in localStorage
      localStorage.setItem('userData', JSON.stringify({
        name: userData.Name,
        username: userData.Username,
        conversation: userData.Conversation || []
      }));
      
      setLoading(false);
      
      // Add success animation
      button.classList.add('success');
      
      // Navigate directly to dashboard after successful login with a short delay
      setTimeout(() => {
        navigate('/dashboard');
      }, 800);
    } catch (err) {
      setLoading(false);
      setError('Invalid username or password');
      
      // Add error shake animation
      const form = document.querySelector('form');
      form.classList.add('shake');
      setTimeout(() => {
        form.classList.remove('shake');
      }, 500);
    }
  };

  return (
    <motion.div 
      className="auth-container"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.6 }}
    >
      <motion.div 
        className="auth-sidebar"
        initial={{ x: -50, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ duration: 0.8 }}
      >
        <motion.h1
          initial={{ y: 20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ delay: 0.3, duration: 0.5 }}
        >
          Skill Seeker
        </motion.h1>
        <motion.div 
          className="dots"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.6, duration: 0.5 }}
        >
          <span className="dot"></span>
          <span className="dot"></span>
          <span className="dot"></span>
        </motion.div>
      </motion.div>
      
      <motion.div 
        className="auth-form-container"
        variants={containerVariants}
        initial="hidden"
        animate="visible"
      >
        <motion.h2 variants={itemVariants}>LOG IN</motion.h2>
        
        {error && (
          <motion.p 
            className="error"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.3 }}
          >
            {error}
          </motion.p>
        )}
        
        <motion.form 
          onSubmit={handleSubmit}
          variants={containerVariants}
        >
          <motion.div className="form-group" variants={itemVariants}>
            <label>Username</label>
            <motion.input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
              whileFocus={{ boxShadow: "0 0 0 3px rgba(37, 99, 235, 0.2)" }}
            />
          </motion.div>
          <motion.div className="form-group" variants={itemVariants}>
            <label>Password</label>
            <motion.input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              whileFocus={{ boxShadow: "0 0 0 3px rgba(37, 99, 235, 0.2)" }}
            />
          </motion.div>
          <motion.button 
            type="submit" 
            className="auth-button" 
            disabled={loading}
            variants={itemVariants}
            whileHover={{ scale: 1.02, boxShadow: "0 4px 12px rgba(37, 99, 235, 0.25)" }}
            whileTap={{ scale: 0.98 }}
          >
            {loading ? (
              <>
                <span className="loading-dots">
                  <span className="dot"></span>
                  <span className="dot"></span>
                  <span className="dot"></span>
                </span>
                Logging in...
              </>
            ) : 'Log in'}
          </motion.button>
        </motion.form>
        
        <motion.p 
          className="auth-redirect"
          variants={itemVariants}
        >
          Don't have an account? <Link to="/signup">Sign Up</Link>
        </motion.p>
      </motion.div>
    </motion.div>
  );
};

export default Login;
