import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { login } from '../services/authService';
import '../styles/Auth.css';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [location, setLocation] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await login(formData);
      setLoading(false);
      setIsLoggedIn(true); // Mark as logged in to show location prompt
    } catch (err) {
      setLoading(false);
      setError(err.response?.data?.message || 'Login failed');
    }
  };

  const handleLocationSubmit = (e) => {
    e.preventDefault();
    // Store location in localStorage
    localStorage.setItem('userLocation', location);
    // Navigate to dashboard
    navigate('/dashboard');
  };

  // If user is logged in, show location prompt
  if (isLoggedIn) {
    return (
      <div className="auth-container">
        <div className="auth-sidebar">
          <h1>Skill Seeker</h1>
          <div className="dots">
            <span className="dot"></span>
            <span className="dot"></span>
            <span className="dot"></span>
          </div>
        </div>
        <div className="auth-form-container">
          <h2>Almost Done!</h2>
          <p>To help us serve you better, please let us know your location:</p>
          <form onSubmit={handleLocationSubmit}>
            <div className="form-group">
              <label>Your Location (City)</label>
              <input
                type="text"
                name="location"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                placeholder="e.g., Islamabad"
                required
              />
            </div>
            <button type="submit" className="auth-button">
              Continue to Dashboard
            </button>
          </form>
        </div>
      </div>
    );
  }

  return (
    <div className="auth-container">
      <div className="auth-sidebar">
        <h1>Skill Seeker</h1>
        <div className="dots">
          <span className="dot"></span>
          <span className="dot"></span>
          <span className="dot"></span>
        </div>
      </div>
      <div className="auth-form-container">
        <h2>LOG IN</h2>
        {error && <p className="error">{error}</p>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="auth-button" disabled={loading}>
            {loading ? 'Logging in...' : 'Log in'}
          </button>
          <button type="button" className="auth-button-secondary">Continue with email</button>
        </form>
        <p className="auth-redirect">
          Don't have an account? <Link to="/signup">Sign Up</Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
