import React, { useState, useEffect, useContext } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import { QueryContext } from '../contexts/QueryContext';
import { AuthContext } from '../contexts/AuthContext';
import '../styles/Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);
  const { currentQuery, setCurrentQuery } = useContext(QueryContext);
  
  const [professionals, setProfessionals] = useState([]);
  const [selectedProfessional, setSelectedProfessional] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [viewMode, setViewMode] = useState('text'); // 'text' or 'chart'
  const [question, setQuestion] = useState('');
  const [questionHistory, setQuestionHistory] = useState([]);

  useEffect(() => {
    // Fetch professionals from the backend
    const fetchProfessionals = async () => {
      try {
        setIsLoading(true);
        setError(null);
        
        // If there's no current query, try to get the user's last saved preference
        let endpoint = '/api/professionals';
        if (currentQuery) {
          endpoint = `/api/professionals/search?query=${encodeURIComponent(currentQuery)}`;
        } else {
          endpoint = '/api/user/preferences/latest';
        }
        
        const response = await fetch(endpoint, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        
        if (!response.ok) {
          throw new Error('Failed to fetch professionals');
        }
        
        const data = await response.json();
        setProfessionals(data.professionals || []);
        
        // Auto-select the first professional if available
        if (data.professionals && data.professionals.length > 0) {
          setSelectedProfessional(data.professionals[0]);
        }
        
      } catch (err) {
        console.error('Error fetching professionals:', err);
        setError('Failed to load professionals. Please try again later.');
      } finally {
        setIsLoading(false);
      }
    };
    
    fetchProfessionals();
  }, [currentQuery]);

  const handleProfessionalSelect = (professional) => {
    setSelectedProfessional(professional);
  };

  const toggleViewMode = () => {
    setViewMode(viewMode === 'text' ? 'chart' : 'text');
  };

  const handleSavePreference = async () => {
    try {
      const response = await fetch('/api/user/preferences/save', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          query: currentQuery,
          professionals: professionals
        })
      });
      
      if (!response.ok) {
        throw new Error('Failed to save preference');
      }
      
      // Success notification
      alert('Your preference has been saved successfully!');
      
    } catch (err) {
      console.error('Error saving preference:', err);
      alert('Failed to save preference. Please try again.');
    }
  };

  const handleQuestionSubmit = async () => {
    if (!question.trim()) return;
    
    // Add question to history
    const newQuestion = {
      id: Date.now(),
      text: question,
      isProcessing: true,
      response: null
    };
    
    setQuestionHistory([...questionHistory, newQuestion]);
    
    try {
      // Call API to get response
      const response = await fetch('/api/question', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          question: question,
          professionalId: selectedProfessional?.ID || null
        })
      });
      
      if (!response.ok) {
        throw new Error('Failed to get response');
      }
      
      const data = await response.json();
      
      // Update question in history with response
      setQuestionHistory(prev => 
        prev.map(q => 
          q.id === newQuestion.id 
            ? { ...q, isProcessing: false, response: data.response } 
            : q
        )
      );
      
    } catch (err) {
      console.error('Error getting response:', err);
      
      // Update question in history with error
      setQuestionHistory(prev => 
        prev.map(q => 
          q.id === newQuestion.id 
            ? { 
                ...q, 
                isProcessing: false, 
                response: "Sorry, there was an error processing your question. Please try again.",
                hasError: true
              } 
            : q
        )
      );
    }
    
    // Clear input
    setQuestion('');
  };

  // ProfessionalCard component inlined to resolve import errors
  const ProfessionalCard = ({ professional, isSelected, onSelect }) => {
    // Determine the professional name based on available fields
    const name = professional.Name || professional['Doctor Name'] || 'Professional';
    // Determine title/expertise based on available fields
    const title = professional['Gig Title'] || professional.Expertise || professional.Category || professional.Skills || '';
    // Determine rating
    const rating = professional.Rating || professional['Success_Rate'] || '4.8';
    // Determine location
    const location = professional.City || (professional.Locations && professional.Locations[0]) || '';
    
    return (
      <div 
        className={`professional-card ${isSelected ? 'selected' : ''}`}
        onClick={onSelect}
      >
        <div className="professional-info">
          <div className="professional-avatar">
            {name.charAt(0)}
          </div>
          <span className="professional-name">{name}</span>
        </div>
        <div className="professional-profession">{title}</div>
        <div className="professional-location">
          <span>{location}</span>
          <span className="more-link">More →</span>
        </div>
      </div>
    );
  };

  // ProfessionalDetail component inlined to resolve import errors
  const ProfessionalDetail = ({ professional, viewMode }) => {
    if (!professional) return null;

    // Extract professional details
    const name = professional.Name || professional['Doctor Name'] || 'Professional';
    const expertise = professional.Expertise || professional.Skills || professional.Category || 'Specialized Professional';
    const experience = professional.Years_of_Experience || professional.Experience || '8+ years';
    const location = (professional.Locations && professional.Locations[0]) || professional.City || professional.Address || 'London, UK';
    const rating = professional.Rating || professional['Success_Rate'] || '4.8';
    const contact = professional.Phone || professional['Contact Number'] || '';
    const education = professional.Education || '';
    const qualifications = professional.Qualifications || '';
    const about = professional.About || '';
    
    return (
      <div className="professional-detail">
        <div className="detail-header">
          <h2>{name}</h2>
          <div className="detail-actions">
            <button className="contact-button">Contact</button>
          </div>
        </div>
        
        <div className="detail-content">
          <div className="detail-section">
            <h3>Professional Information</h3>
            <div className="info-item">
              <span className="info-label">Expertise:</span>
              <span className="info-value">{expertise}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Experience:</span>
              <span className="info-value">{experience}</span>
            </div>
            <div className="info-item">
              <span className="info-label">Location:</span>
              <span className="info-value">{location}</span>
            </div>
            {rating && (
              <div className="info-item">
                <span className="info-label">Rating:</span>
                <span className="info-value rating">
                  <span className="stars">★★★★★</span>
                  <span className="rating-value">{rating}</span>
                </span>
              </div>
            )}
            {contact && (
              <div className="info-item">
                <span className="info-label">Contact:</span>
                <span className="info-value">{contact}</span>
              </div>
            )}
          </div>
          
          {(education || qualifications || about) && (
            <div className="detail-section">
              <h3>Additional Information</h3>
              {education && (
                <div className="info-item">
                  <span className="info-label">Education:</span>
                  <span className="info-value">{education}</span>
                </div>
              )}
              {qualifications && (
                <div className="info-item">
                  <span className="info-label">Qualifications:</span>
                  <span className="info-value">{qualifications}</span>
                </div>
              )}
              {about && (
                <div className="info-item">
                  <span className="info-label">About:</span>
                  <span className="info-value">{about}</span>
                </div>
              )}
            </div>
          )}
          
          {viewMode === 'chart' && (
            <div className="detail-section">
              <h3>Skills & Expertise</h3>
              <div className="skills-chart">
                {/* Placeholder for skills chart - would be replaced with actual chart component */}
                <div className="chart-placeholder">
                  <p>Skills visualization would appear here in chart view</p>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    );
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Welcome, {user?.Name || 'User'}</h1>
        
        <div className="dashboard-actions">
          <motion.button 
            className="action-button save-button"
            onClick={handleSavePreference}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Save Preference
          </motion.button>
          
          <motion.button 
            className="action-button view-button"
            onClick={toggleViewMode}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            {viewMode === 'text' ? 'Chart View' : 'Text View'}
          </motion.button>
        </div>
      </div>
      
      <div className="dashboard-content">
        {/* Left Sidebar - Professional List */}
        <aside className="professionals-sidebar">
          <h2>Recommended Professionals</h2>
          
          {isLoading ? (
            <div className="loading-spinner-container">
              <div className="loading-spinner"></div>
              <p>Loading professionals...</p>
            </div>
          ) : error ? (
            <div className="error-message">
              <p>{error}</p>
              <button 
                onClick={() => window.location.reload()}
                className="retry-button"
              >
                Retry
              </button>
            </div>
          ) : professionals.length === 0 ? (
            <div className="empty-state">
              <p>No professionals found. Try a different query.</p>
            </div>
          ) : (
            <div className="professionals-list">
              <AnimatePresence>
                {professionals.map((professional, index) => (
                  <motion.div
                    key={professional.ID || index}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -20 }}
                    transition={{ duration: 0.3, delay: index * 0.1 }}
                  >
                    <ProfessionalCard
                      professional={professional}
                      isSelected={selectedProfessional && selectedProfessional.ID === professional.ID}
                      onSelect={() => handleProfessionalSelect(professional)}
                    />
                  </motion.div>
                ))}
              </AnimatePresence>
            </div>
          )}
        </aside>
        
        {/* Middle - Professional Details */}
        <main className="professional-detail-area">
          {selectedProfessional ? (
            <ProfessionalDetail 
              professional={selectedProfessional} 
              viewMode={viewMode}
            />
          ) : (
            <div className="empty-detail-state">
              <h3>Select a professional to view details</h3>
              <p>Browse the list of recommended professionals on the left.</p>
            </div>
          )}
        </main>
        
        {/* Right Sidebar - Ask Additional Questions */}
        <div className="questions-sidebar">
          <div className="questions-panel">
            <h2 className="questions-title">Ask Additional Questions</h2>
            
            <div className="questions-history">
              {questionHistory.map(item => (
                <div key={item.id} className="question-item">
                  <div className="question-text">
                    <p className="question-label">You asked:</p>
                    <p>{item.text}</p>
                  </div>
                  
                  <div className="question-response">
                    {item.isProcessing ? (
                      <div className="processing-indicator">
                        <div className="dot-pulse"></div>
                      </div>
                    ) : (
                      <div className={`response-content ${item.hasError ? 'has-error' : ''}`}>
                        {item.response}
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
            
            <div className="question-input-area">
              <textarea
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                placeholder="Ask a question about the project or professional..."
                className="question-input"
                onKeyDown={(e) => {
                  if (e.key === 'Enter' && e.ctrlKey) {
                    handleQuestionSubmit();
                  }
                }}
              />
              <div className="input-actions">
                <button 
                  className="send-button"
                  onClick={handleQuestionSubmit}
                  disabled={!question.trim()}
                >
                  Send
                </button>
              </div>
              <div className="input-help">
                <p>Press Ctrl+Enter to send</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
