// src/pages/QueryProcessing.js (updated with animations)
import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion'; // Add this import
import Header from '../components/Header';
import Footer from '../components/Footer';
import Loading from '../components/Loading';
import { processQuery, submitAnswers } from '../services/queryService';
import '../styles/QueryProcessing.css';

const QueryProcessing = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { category, query } = location.state || {};
  
  const [processId, setProcessId] = useState(null);
  const [currentStep, setCurrentStep] = useState('loading');
  const [synthesis, setSynthesis] = useState('');
  const [followUpQuestions, setFollowUpQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [professionals, setProfessionals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  
  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { duration: 0.5, when: "beforeChildren", staggerChildren: 0.1 }
    },
    exit: { opacity: 0, transition: { duration: 0.3 } }
  };
  
  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: { y: 0, opacity: 1, transition: { duration: 0.4 } }
  };
  
  const questionVariants = {
    hidden: { x: -20, opacity: 0 },
    visible: custom => ({
      x: 0, 
      opacity: 1, 
      transition: { 
        delay: custom * 0.1,
        duration: 0.4 
      }
    })
  };
  
  useEffect(() => {
    const processUserQuery = async () => {
      setLoading(true);
      try {
        // Filter out location information from query 
        let queryText = '';
        
        if (typeof query === 'string') {
          queryText = query;
        } else if (typeof query === 'object' && query !== null) {
          // If query is an object, convert it to string or use relevant property
          queryText = query.text || JSON.stringify(query);
        } else {
          queryText = String(query || '');
        }
        
        // List of locations to filter out
        const knownLocations = [
          'Lahore', 'Karachi', 'Islamabad', 'Rawalpindi', 'Faisalabad',
          'Multan', 'Peshawar', 'Quetta', 'Sialkot', 'Gujranwala',
        ];
        
        // Remove known locations from query
        knownLocations.forEach(location => {
          queryText = queryText.replace(new RegExp('\\b' + location + '\\b', 'gi'), '');
        });
        
        // Clean up extra spaces
        queryText = queryText.replace(/\s+/g, ' ').trim();
        
        console.log('Query after location filtering:', queryText);
        
        // Start the process and get initial synthesis and questions
        const response = await processQuery(category, queryText);
        
        if (response.data.status === 'questions') {
          setProcessId(response.data.processId);
          setSynthesis(response.data.synthesis);
          setFollowUpQuestions(response.data.questions);
          
          // Add a small delay for the loading animation
          setTimeout(() => {
            setCurrentStep('questions');
            setLoading(false);
          }, 800);
        } else {
          setError('Unexpected response format');
          setLoading(false);
        }
      } catch (err) {
        setError('Failed to process your query: ' + (err.response?.data?.message || err.message));
        console.error(err);
        setLoading(false);
      }
    };
    
    if (category && query) {
      processUserQuery();
    } else {
      setError('Missing required information. Please go back and try again.');
      setLoading(false);
    }
  }, [category, query]);
  
  const handleAnswerChange = (question, answer) => {
    setAnswers({
      ...answers,
      [question]: answer
    });
  };
  
  const handleSubmitAnswers = async () => {
    if (!processId) {
      setError('No active process found');
      return;
    }
    
    setLoading(true);
    try {
      // Add button loading animation
      const button = document.querySelector('.submit-answers-button');
      button.classList.add('loading');
      
      const response = await submitAnswers(processId, answers);
      
      if (response.data.status === 'results') {
        setProfessionals(response.data.professionals);
        
        // Add success animation
        button.classList.remove('loading');
        button.classList.add('success');
        
        // Add a small delay for the animation to complete
        setTimeout(() => {
          setCurrentStep('results');
          setLoading(false);
        }, 800);
      } else {
        setError('Unexpected response format');
        setLoading(false);
      }
    } catch (err) {
      setError('Failed to process answers: ' + (err.response?.data?.message || err.message));
      console.error(err);
      setLoading(false);
    }
  };
  
  const handleViewResults = () => {
    // Add button click animation
    const button = document.querySelector('.view-results-button');
    button.classList.add('clicked');
    
    // Navigate with a small delay for animation
    setTimeout(() => {
      navigate('/results', { 
        state: { professionals, category, query, synthesis, followUpQA: answers } 
      });
    }, 400);
  };
  
 // src/pages/QueryProcessing.js (continued)
 return (
  <motion.div 
    className="query-processing-page"
    initial={{ opacity: 0 }}
    animate={{ opacity: 1 }}
    exit={{ opacity: 0 }}
  >
    <Header />
    
    <motion.main 
      className="processing-container"
      variants={containerVariants}
      initial="hidden"
      animate="visible"
      exit="exit"
    >
      {error ? (
        <motion.div 
          className="error-container"
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ duration: 0.4 }}
        >
          <h2>Error</h2>
          <p>{error}</p>
          <motion.button 
            onClick={() => navigate('/dashboard')}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Back to Dashboard
          </motion.button>
        </motion.div>
      ) : currentStep === 'loading' ? (
        <Loading message="Analyzing your query..." />
      ) : currentStep === 'questions' ? (
        <AnimatePresence>
          <motion.div
            key="questions-step"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            <motion.h2 variants={itemVariants}>Project Analysis</motion.h2>
            <motion.div 
              className="synthesis-box"
              variants={itemVariants}
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.2 }}
            >
              <h3>Our Understanding</h3>
              <p>{synthesis}</p>
            </motion.div>
            
            <motion.h3 
              variants={itemVariants}
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.4 }}
            >
              We need a few more details
            </motion.h3>
            
            <motion.div 
              className="questions-container"
              variants={containerVariants}
            >
              {followUpQuestions.map((question, index) => (
                <motion.div 
                  key={index} 
                  className="question-box"
                  variants={questionVariants}
                  custom={index}
                  initial="hidden"
                  animate="visible"
                  whileHover={{ scale: 1.02, boxShadow: "0 8px 20px rgba(0, 0, 0, 0.1)" }}
                >
                  <p className="question-text">{question}</p>
                  <motion.textarea
                    placeholder="Your answer..."
                    value={answers[question] || ''}
                    onChange={(e) => handleAnswerChange(question, e.target.value)}
                    rows={3}
                    whileFocus={{ boxShadow: "0 0 0 3px rgba(37, 99, 235, 0.2)" }}
                  />
                </motion.div>
              ))}
              
              <motion.button 
                onClick={handleSubmitAnswers}
                disabled={Object.keys(answers).length < followUpQuestions.length || loading}
                className="submit-answers-button"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.5 + (followUpQuestions.length * 0.1) }}
                whileHover={{ scale: 1.05, boxShadow: "0 8px 20px rgba(37, 99, 235, 0.25)" }}
                whileTap={{ scale: 0.95 }}
              >
                {loading ? (
                  <>
                    <span className="loading-spinner"></span>
                    Processing...
                  </>
                ) : 'Find Matching Professionals'}
              </motion.button>
            </motion.div>
          </motion.div>
        </AnimatePresence>
      ) : currentStep === 'results' ? (
        <motion.div 
          className="results-found"
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ duration: 0.6, type: "spring" }}
        >
          <motion.h2
            initial={{ y: -20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.2 }}
          >
            Matching Professionals Found!
          </motion.h2>
          <motion.p
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.4 }}
          >
            We've identified the best professionals for your needs.
          </motion.p>
          <motion.button 
            onClick={handleViewResults} 
            className="view-results-button"
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.6 }}
            whileHover={{ scale: 1.05, boxShadow: "0 8px 20px rgba(37, 99, 235, 0.25)" }}
            whileTap={{ scale: 0.95 }}
          >
            View Recommended Professionals
          </motion.button>
        </motion.div>
      ) : null}
    </motion.main>
    
    <Footer />
    
    {(currentStep === 'loading' || loading) && <Loading message="Processing..." />}
  </motion.div>
);
};

export default QueryProcessing;
